package day1;

public class ExampleSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// important to add break statement.
		
		int j=3;
		switch(j) {
		
		case 0:
		System.out.println("Value of J is zero");
		break;
		case 1:
			System.out.println("Value of J is One");
			break;
		case 2:
			System.out.println("Value of J is two");
			break;
		case 3:
			System.out.println("Value of J is three");
			break;
		default:
		
			System.out.println("Value of J is Four");
			//break;
		
		}

	}

}
